/*
** ===================================================================
**     Method      :  ConsoleRx_Peek (component RingBuffer)
**     Description :
**         Returns an element of the buffer without removiing it.
**     Parameters  :
**         NAME            - DESCRIPTION
**         index           - Index of element. 0 peeks the top
**                           element, 1 the next, and so on.
**       * elemP           - Pointer to where to store the received
**                           element
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
uint8_t ConsoleRx_Peek(ConsoleRx_BufSizeType index, ConsoleRx_ElementType *elemP)
{
  uint8_t res = ERR_OK;
  int idx; /* index inside ring buffer */
  ConsoleRx_DEFINE_CRITICAL();

  ConsoleRx_ENTER_CRITICAL();
  if (index>=ConsoleRx_CONFIG_BUF_SIZE) {
    res = ERR_OVERFLOW; /* asking for an element outside of ring buffer size */
  } else if (index<ConsoleRx_inSize) {
    idx = (ConsoleRx_outIdx+index)%ConsoleRx_CONFIG_BUF_SIZE;
    *elemP = ConsoleRx_buffer[idx];
  } else { /* asking for an element which does not exist */
    res = ERR_RXEMPTY;
  }
  ConsoleRx_EXIT_CRITICAL();
  return res;
}

/*
** ===================================================================
**     Method      :  ConsoleRx_Compare (component RingBuffer)
**     Description :
**         Compares the elements in the buffer.
**     Parameters  :
**         NAME            - DESCRIPTION
**         index           - Index of element. 0 peeks the top
**                           element, 1 the next, and so on.
**       * elemP           - Pointer to elements to compare with
**         nof             - number of elements to compare
**     Returns     :
**         ---             - zero if elements are the same, -1 otherwise
** ===================================================================
*/
uint8_t ConsoleRx_Compare(ConsoleRx_BufSizeType index, ConsoleRx_ElementType *elemP, ConsoleRx_BufSizeType nof)
{
  uint8_t cmpResult = 0;
  uint8_t res;
  ConsoleRx_ElementType val;

  while(nof>0) {
    res = ConsoleRx_Peek(index, &val);
    if (res!=ERR_OK) { /* general failure? */
      cmpResult = (uint8_t)-1; /* no match */
      break;
    }
    if (val!=*elemP) { /* mismatch */
      cmpResult = (uint8_t)-1; /* no match */
      break;
    }
    elemP++; index++; nof--;
  }

  return cmpResult;
}

